    const fetchData = async () => {
      try {
        const response = await fetch(`https://restcountries.com/v3.1/all`);
        const jsonData = await response.json();
        // setData(jsonData);

        setSelectedData(jsonData[id]);
        console.log(jsonData[id]);
        // console.log(data[id].name.official);
      } catch (error) {
        console.error("Error:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {selectedData !== null ? (
        <div>{selectedData.name.official}</div>
      ) : (
        <div>Loading...</div>
      )}
      {/* <div style={{ paddingTop: "9%" }}>{if selectedData.name.official}</div> */}
    </div>
  );
}

export default Details;
